import { Component } from '@angular/core';

@Component({
  selector: 'app-modal',
  templateUrl: './modal.component.html',
  styleUrls: ['./modal.component.css']
})
export class ModalComponent {
  hideModal: boolean = true;

  Close(){
    console.log("Close");
  }

}



<div class="position-absolute" id="myModal" [hidden]="true">
  <div class="bg-light border row">
    <div class="col-6 mt-2">
      Новая категория
    </div>
    <div class="col-6 mt-2 text-end">
      <button class="btn-danger" (click)="Close()">
        <i class="fa fa-window-close" aria-hidden="true"></i>
      </button>
    </div>
    <div class="col-12 mt-2">
      <input class="form-control form-control-sm" type="text" placeholder="Категория" aria-label="Category">
    </div>
    <div class="col-12 mt-2 mb-2 text-center">
      <button class="btn-primary">Добавить категорию</button>
    </div>
  </div>
</div>
