import { Component } from '@angular/core';
import { Category } from '../../modal/category.mode';
import { Product } from '../../modal/product.mode';

@Component({
  selector: 'app-productadd',
  templateUrl: './productadd.component.html',
  styleUrls: ['./productadd.component.css']
})
export class ProductaddComponent {
  hideModal: boolean = false;
  categorys: Category[] = [{ id: 1, name: 'Cat1' }, { id: 2, name: 'Cat2' }, { id: 3, name: 'Cat3' }]
  types: Category[] = [{ id: 1, name: 'Type1' }, { id: 2, name: 'Type2' }, { id: 3, name: 'Type3' }]

  product: Product = {
    id: 0,
    name: '',
    shortDes: '',
    description: '',
    image: '',
    price: 0,
    categoryId: 1,
    applicationTypeId:1
  }

  addProduct() {
    console.log(this.product);
  }

  Close() {
    this.hideModal = !this.hideModal
  }

}
