  <div class="spinner-grow me-2" style="width: 1rem; height: 1rem;" role="status">
    <span class="visually-hidden">Загрузка...</span>
  </div>
  <div class="spinner-grow me-2" style="width: 1rem; height: 1rem;" role="status">
    <span class="visually-hidden">Загрузка...</span>
  </div>
  <div class="spinner-grow me-2" style="width: 1rem; height: 1rem;" role="status">
    <span class="visually-hidden">Загрузка...</span>
  </div>
</div>



<div *ngIf="loading">
  <div class="row">
    <div class="col-6">
      <h1>Категории</h1>
    </div>
    <div class="col-6 text-end">
      <app-modal></app-modal>
    </div>
  </div>

<table *ngIf="loading" class='table table-striped' width="10%" aria-labelledby="tableLabel">
  <thead>
    <tr>
      <th>Id</th>
      <th>Name</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let category of categories">
      <td>{{ category.id }}</td>
      <td>{{ category.name }}</td>
      <td><button type="button" (click)="deleteCategory(category.id)"  class="btn btn-danger"><i class="fa fa-trash" aria-hidden="true"></i></button> </td>
    </tr>
  </tbody>
</table>
  </div>
