import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Router } from '@angular/router';
import { Category } from '../../modal/category.mode';
import { EventEmitterService } from '../event-emitter.service';

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent {
  public hideModal: boolean = false;
  @Input() IsActive: boolean | undefined;
    @Input() public category: Category = {
    id: 0,
    name: 'none'
    };

  @Input() categoryLison = new EventEmitter(); 

  constructor(private eventEmitterService: EventEmitterService, public router: Router) {
    console.log();
  }

  showActive() {
    console.log(this.IsActive)
  }

  myMethod(isActive:any) {
    console.log("!!!!!" + this.IsActive );
  }

  public editCategory() {
    this.eventEmitterService.onEditClick(this.category);
    this.Close();
  }

  Close() {
    this.hideModal = !this.hideModal;
  }


}
