import { HttpClient } from '@angular/common/http';
import { Component, EventEmitter, Inject, Output } from '@angular/core';
import { ApplicationType } from '../../modal/applicationtype.mode';
import { Category } from '../../modal/category.mode';
import { Product } from '../../modal/product.mode';

@Component({
  selector: 'app-productadd',
  templateUrl: './productadd.component.html',
  styleUrls: ['./productadd.component.css']
})
export class ProductaddComponent {
  hideModal: boolean = false;
  http: HttpClient;
  baseUrl: string;
  categorys: Category[] = [];
  types: ApplicationType[] = [];
  @Output() productToAdd = new EventEmitter();

  Aptype: ApplicationType = {
    id: 0,
    name: ''
  }
  category: ApplicationType = {
    id: 0,
    name: ''
  }


  product: Product = {
      id: 0,
      name: '',
      shortDes: '',

  
      description: '',
      image: '',
      price: 0,
 

      tempCount: 1
  }

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string){
    this.http = http;
    this.baseUrl = baseUrl;

    this.http.get<Category[]>(this.baseUrl + 'Category').subscribe(result => {
      this.categorys = result;
    }, error => console.error(error));

    this.http.get<ApplicationType[]>(this.baseUrl + 'Type').subscribe(result => {
      this.types = result;
    });

  }

  isFillProduct() {
    if (this.product.name == '' || this.product.shortDes == '' || this.product.description == '' || this.product.image == '' || this.product.price<=0 )
      return false;
    return true;
  }

  AlertMessage() {
    alert("Пожалуйста, запролните все поля"); 
  }

  addProduct() {
    this.productToAdd.emit(this.product);
  }

  Close() {
    this.hideModal = !this.hideModal
  }

}
<button class="btn btn-primary" (click)="Close()"><i class="fa fa-plus">&nbsp;</i>Добавить товар</button>



<div *ngIf="hideModal" class="bg-dark position-fixed start-0 end-0 top-0 bottom-0 opacity-25"></div>

<div *ngIf="hideModal" class="position-absolute col-md-3 translate-middle top-50">
  <div class="bg-light row rounded">
    <div class="col-6 mt-2 text-start">
      <p class="font-weight-bold">Новый продукт</p>
    </div>
    <div class="col-6 mt-2 text-end">
      <button type="button" class="btn-close" (click)="Close()" aria-label="Close"></button>
    </div>
    <div class="col-12 mt-2">

      <div class="text-start">
        <span>Название продукта:</span>
      </div>
      <input class="form-control form-control-sm" type="text" id="name" [(ngModel)]='product.name' [ngModelOptions]="{standalone: true}" placeholder="Название продукта" aria-label="Product">
      <span *ngIf="product.name.length<3" class="text-danger text-danger">Количество символов меньше 3</span>

      <div class="text-start">
        <span>Краткое описание:</span>
      </div>
      <textarea [(ngModel)]="product.shortDes" class="summernote form-control"></textarea>
      <span *ngIf="product.shortDes.length<3" class="text-danger text-danger">Количество символов меньше 3</span>

      <div class="text-start">
        <span>Описание:</span>
      </div>
      <textarea [(ngModel)]="product.description" class="summernote form-control"></textarea>
      <span *ngIf="product.description.length<3" class="text-danger text-danger">Количество символов меньше 3</span>



      <div class="text-start">
        <span>Изображение:</span>
      </div>
      <input [(ngModel)]="product.image" type="file" name="files" id="uploadBox" multiple class="form-control" />
      <span *ngIf="product.image.length<3" class="text-danger text-danger">Количество символов меньше 3</span>

      <div class="text-start">
        <span>Цена:</span>
      </div>
      <input class="form-control form-control-sm" type="text" id="name" [(ngModel)]='product.price' [ngModelOptions]="{standalone: true}" placeholder="Цена" aria-label="Product">
      <span *ngIf="" class="text-danger text-danger">Количество символов меньше 3</span>

     

    </div>
    <div class="col-12 mt-2 mb-2 text-center">
      <button class="btn btn-primary" (click)="isFillProduct()?addProduct(): AlertMessage()">Добавить тип</button>
    </div>
  </div>
</div>
