import { Component, EventEmitter, Inject, Injectable, Input, OnInit, Output} from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { EventEmitterService } from './event-emitter.service';
import { Category } from '../modal/category.mode';
import { delay } from 'rxjs';

@Component({
  selector: 'category-data',
  templateUrl: './category.component.html'
})

  @Injectable({
    providedIn: 'root'
  })

export class CategoryComponent implements OnInit{
  categories: Category[] = [];
  private baseUrl: string = "";
  loading: boolean = false;
  http: HttpClient;
  @Output() categoryT = new EventEmitter();


  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string, private eventEmitterService: EventEmitterService) {
    this.baseUrl = baseUrl;
    this.http = http;
    this.getCategory();
  }
    ngOnInit(): void {
      if (this.eventEmitterService.subsVar == undefined) {
        this.eventEmitterService.subsVar = this.eventEmitterService.
          invokeFirstComponentFunction.subscribe((name: string) => {
            console.log("ConsoleLOG" + this.eventEmitterService.category.id + this.eventEmitterService.category.name);
            this.addCategory(this.eventEmitterService.category);
          });
      } 
  }

  MyMethod(category: Category) {
      this.categoryT.emit(category); 
  }

  addCategory(category: Category) {
    this.loading = false;
    return this.http.post<Category>(this.baseUrl + 'Category/', category).subscribe(result => {
      console.log("ConsoleLOG" + result);
      this.getCategory();
    });
  }

  getCategory() {
    this.http.get<Category[]>(this.baseUrl + 'Category').subscribe(result => {
      this.categories = result;
      this.loading = true;
    }, error => console.error(error));
  }

  deleteCategory(id: number) {
    this.loading = false;
    return this.http.delete<Category[]>(this.baseUrl + 'Category/' + id).subscribe(result => {
      this.getCategory();
    });
  }

}


<div *ngIf="!loading" class="d-flex justify-content-center">
  <app-loading></app-loading>
</div>



<div *ngIf="loading">

  <div class="row ms-1">
    <div class="col-6">
      <h1 class="font-weight-bold">Категории</h1>
    </div>
    <div class="col-6 text-end">
        <app-modal></app-modal>
      </div>
    </div>


    <div class="row border border-dark ms-3">
      <table *ngIf="loading" class='table table-striped' aria-labelledby="tableLabel">
        <thead>
          <tr>
            <th>Id</th>
            <th>Name</th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let category of categories">
            <td>{{ category.id }}</td>
            <td>{{ category.name }}</td>
              <td class="text-end">
                <app-edit (categoryT)="MyMethod($category)"></app-edit>
              </td>
            <td class="text-end">
              <button type="button" (click)="deleteCategory(category.id)" class="btn btn-outline-danger"><i class="fa fa-trash" aria-hidden="true"></i></button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>

  </div>
